mapper.delete:

delete from base_call_history


    and emp_sources_id = #{emp_sources_id,jdbcType=NVARCHAR}


    and contact_id = #{contact_id,jdbcType=INTEGER}


    and call_time = #{call_time,jdbcType=TIMESTAMP}


    and hang_time = #{hang_time,jdbcType=TIMESTAMP}


    and record_num = #{record_num,jdbcType=NVARCHAR}


    and ob_req_id = #{ob_req_id,jdbcType=INTEGER}


    and phone_num = #{phone_num,jdbcType=NVARCHAR}


    and remark = #{remark,jdbcType=NVARCHAR}

mapper.select:

select id, emp_sources_id, contact_id, call_time, hang_time, record_num, ob_req_id, 
phone_num, remark
from base_call_history


    and emp_sources_id = #{emp_sources_id,jdbcType=NVARCHAR}


    and contact_id = #{contact_id,jdbcType=INTEGER}


    and call_time = #{call_time,jdbcType=TIMESTAMP}


    and hang_time = #{hang_time,jdbcType=TIMESTAMP}


    and record_num = #{record_num,jdbcType=NVARCHAR}


    and ob_req_id = #{ob_req_id,jdbcType=INTEGER}


    and phone_num = #{phone_num,jdbcType=NVARCHAR}


    and remark = #{remark,jdbcType=NVARCHAR}
