mapper.delete:

delete from base_comp_sync


    and hr_comp_code = #{hr_comp_code,jdbcType=NVARCHAR}


    and erp_entity_code = #{erp_entity_code,jdbcType=NVARCHAR}


    and erp_comp_id = #{erp_comp_id,jdbcType=NVARCHAR}


    and erp_comp_code = #{erp_comp_code,jdbcType=NVARCHAR}


    and erp_comp_name = #{erp_comp_name,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}

mapper.select:

select id, hr_comp_code, erp_entity_code, erp_comp_id, erp_comp_code, erp_comp_name, 
enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark, sync_datetime
from base_comp_sync


    and hr_comp_code = #{hr_comp_code,jdbcType=NVARCHAR}


    and erp_entity_code = #{erp_entity_code,jdbcType=NVARCHAR}


    and erp_comp_id = #{erp_comp_id,jdbcType=NVARCHAR}


    and erp_comp_code = #{erp_comp_code,jdbcType=NVARCHAR}


    and erp_comp_name = #{erp_comp_name,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}
