mapper.delete:

delete from base_knowledge


    and knowledge_name = #{knowledge_name,jdbcType=NVARCHAR}


    and knowledge_category = #{knowledge_category,jdbcType=NVARCHAR}


    and knowledge_question_category = #{knowledge_question_category,jdbcType=NVARCHAR}


    and knowledge_question_child_category = #{knowledge_question_child_category,jdbcType=NVARCHAR}


    and knowledge_question_third_category = #{knowledge_question_third_category,jdbcType=NVARCHAR}


    and use_scope = #{use_scope,jdbcType=NVARCHAR}


    and is_comp_seen = #{is_comp_seen,jdbcType=CHAR}


    and question_desc = #{question_desc,jdbcType=NVARCHAR}


    and solve_desc = #{solve_desc,jdbcType=NVARCHAR}


    and knowledge_status = #{knowledge_status,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, knowledge_name, knowledge_category, knowledge_question_category, knowledge_question_child_category, 
knowledge_question_third_category, use_scope, is_comp_seen, question_desc, solve_desc, 
knowledge_status, enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark
from base_knowledge


    and knowledge_name = #{knowledge_name,jdbcType=NVARCHAR}


    and knowledge_category = #{knowledge_category,jdbcType=NVARCHAR}


    and knowledge_question_category = #{knowledge_question_category,jdbcType=NVARCHAR}


    and knowledge_question_child_category = #{knowledge_question_child_category,jdbcType=NVARCHAR}


    and knowledge_question_third_category = #{knowledge_question_third_category,jdbcType=NVARCHAR}


    and use_scope = #{use_scope,jdbcType=NVARCHAR}


    and is_comp_seen = #{is_comp_seen,jdbcType=CHAR}


    and question_desc = #{question_desc,jdbcType=NVARCHAR}


    and solve_desc = #{solve_desc,jdbcType=NVARCHAR}


    and knowledge_status = #{knowledge_status,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}
