the_title, person_tel, person_email, is_cust_sign, authorized_person, is_authorized_stop, 
authorized_stop_desc, person_remark, enable_flag, create_user, create_timestamp, 
last_update_user, last_update_timestamp, last_update_remark, sync_datetime, source_id
