mapper.select:

select id, property_code, property_name, property_address, key_property_code, property_tel, 
property_email, property_address_id, mt_key_property, enable_flag, create_user, create_timestamp, 
last_update_user, last_update_timestamp, last_update_remark, sync_datetime, source_id, 
is_sync, comp_id
from base_property


    and property_code = #{property_code,jdbcType=NVARCHAR}


    and property_name = #{property_name,jdbcType=NVARCHAR}


    and property_address = #{property_address,jdbcType=NVARCHAR}


    and key_property_code = #{key_property_code,jdbcType=NVARCHAR}


    and property_tel = #{property_tel,jdbcType=NVARCHAR}


    and property_email = #{property_email,jdbcType=NVARCHAR}


    and property_address_id = #{property_address_id,jdbcType=NVARCHAR}


    and mt_key_property = #{mt_key_property,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}


    and is_sync = #{is_sync,jdbcType=CHAR}


    and comp_id = #{comp_id,jdbcType=INTEGER}
