mapper.delete:

delete from base_property_person


    and property_id = #{property_id,jdbcType=INTEGER}


    and person_id = #{person_id,jdbcType=NVARCHAR}


    and person_name = #{person_name,jdbcType=NVARCHAR}


    and person_position = #{person_position,jdbcType=NVARCHAR}


    and person_tel1 = #{person_tel1,jdbcType=NVARCHAR}


    and person_tel2 = #{person_tel2,jdbcType=NVARCHAR}


    and person_email = #{person_email,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}

mapper.select:

select id, property_id, person_id, person_name, person_position, person_tel1, person_tel2, 
person_email, enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark, sync_datetime, source_id
from base_property_person


    and property_id = #{property_id,jdbcType=INTEGER}


    and person_id = #{person_id,jdbcType=NVARCHAR}


    and person_name = #{person_name,jdbcType=NVARCHAR}


    and person_position = #{person_position,jdbcType=NVARCHAR}


    and person_tel1 = #{person_tel1,jdbcType=NVARCHAR}


    and person_tel2 = #{person_tel2,jdbcType=NVARCHAR}


    and person_email = #{person_email,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}
