mapper.delete:

delete from base_skill_evaluation


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and skill_name = #{skill_name,jdbcType=NVARCHAR}


    and skill_type = #{skill_type,jdbcType=NVARCHAR}


    and skill_category = #{skill_category,jdbcType=NVARCHAR}


    and skill_level = #{skill_level,jdbcType=NVARCHAR}


    and skill_pass_date = #{skill_pass_date,jdbcType=DATE}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}

mapper.select:

select id, emp_code, skill_name, skill_type, skill_category, skill_level, skill_pass_date, 
enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark, sync_datetime, source_id
from base_skill_evaluation


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and skill_name = #{skill_name,jdbcType=NVARCHAR}


    and skill_type = #{skill_type,jdbcType=NVARCHAR}


    and skill_category = #{skill_category,jdbcType=NVARCHAR}


    and skill_level = #{skill_level,jdbcType=NVARCHAR}


    and skill_pass_date = #{skill_pass_date,jdbcType=DATE}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}
