mapper.delete:

delete from base_training


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and train_year = #{train_year,jdbcType=CHAR}


    and train_name = #{train_name,jdbcType=NVARCHAR}


    and course_name = #{course_name,jdbcType=NVARCHAR}


    and train_method = #{train_method,jdbcType=NVARCHAR}


    and train_type = #{train_type,jdbcType=NVARCHAR}


    and train_begin_date = #{train_begin_date,jdbcType=DATE}


    and train_end_date = #{train_end_date,jdbcType=DATE}


    and train_result = #{train_result,jdbcType=NVARCHAR}


    and train_organization = #{train_organization,jdbcType=NVARCHAR}


    and emp_unit = #{emp_unit,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}

mapper.select:

select id, emp_code, train_year, train_name, course_name, train_method, train_type, 
train_begin_date, train_end_date, train_result, train_organization, emp_unit, enable_flag, 
create_user, create_timestamp, last_update_user, last_update_timestamp, last_update_remark, 
sync_datetime, source_id
from base_training


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and train_year = #{train_year,jdbcType=CHAR}


    and train_name = #{train_name,jdbcType=NVARCHAR}


    and course_name = #{course_name,jdbcType=NVARCHAR}


    and train_method = #{train_method,jdbcType=NVARCHAR}


    and train_type = #{train_type,jdbcType=NVARCHAR}


    and train_begin_date = #{train_begin_date,jdbcType=DATE}


    and train_end_date = #{train_end_date,jdbcType=DATE}


    and train_result = #{train_result,jdbcType=NVARCHAR}


    and train_organization = #{train_organization,jdbcType=NVARCHAR}


    and emp_unit = #{emp_unit,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


