mapper.delete:

delete from cal_call_sms


    and service_order = #{service_order,jdbcType=CHAR}


    and emp_name = #{emp_name,jdbcType=NVARCHAR}


    and id_number = #{id_number,jdbcType=NVARCHAR}


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and person_tel = #{person_tel,jdbcType=NVARCHAR}


    and comp_id = #{comp_id,jdbcType=INTEGER}


    and stat_id = #{stat_id,jdbcType=INTEGER}


    and emp_position = #{emp_position,jdbcType=NVARCHAR}


    and emp_type = #{emp_type,jdbcType=CHAR}


    and sms_content = #{sms_content,jdbcType=NVARCHAR}


    and send_datetime = #{send_datetime,jdbcType=TIMESTAMP}


    and send_person = #{send_person,jdbcType=NVARCHAR}


    and fault_reason = #{fault_reason,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, service_order, emp_name, id_number, emp_code, person_tel, comp_id, stat_id, 
emp_position, emp_type, sms_content, send_datetime, send_person, fault_reason, enable_flag, 
create_user, create_timestamp, last_update_user, last_update_timestamp, last_update_remark
from cal_call_sms


    and service_order = #{service_order,jdbcType=CHAR}


    and emp_name = #{emp_name,jdbcType=NVARCHAR}


    and id_number = #{id_number,jdbcType=NVARCHAR}


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and person_tel = #{person_tel,jdbcType=NVARCHAR}


    and comp_id = #{comp_id,jdbcType=INTEGER}


    and stat_id = #{stat_id,jdbcType=INTEGER}


    and emp_position = #{emp_position,jdbcType=NVARCHAR}


    and emp_type = #{emp_type,jdbcType=CHAR}


    and sms_content = #{sms_content,jdbcType=NVARCHAR}


    and send_datetime = #{send_datetime,jdbcType=TIMESTAMP}


    and send_person = #{send_person,jdbcType=NVARCHAR}


    and fault_reason = #{fault_reason,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


