mapper.delete:

delete from cal_rota


    and begin_office_date = #{begin_office_date,jdbcType=DATE}


    and end_office_date = #{end_office_date,jdbcType=DATE}


    and begin_time = #{begin_time,jdbcType=CHAR}


    and end_time = #{end_time,jdbcType=CHAR}


    and comp_id = #{comp_id,jdbcType=INTEGER}


    and stat_id = #{stat_id,jdbcType=INTEGER}


    and area_id = #{area_id,jdbcType=INTEGER}


    and group_id = #{group_id,jdbcType=INTEGER}


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and rota_level = #{rota_level,jdbcType=CHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, begin_office_date, end_office_date, begin_time, end_time, comp_id, stat_id, 
area_id, group_id, emp_code, rota_level, enable_flag, create_user, create_timestamp, 
last_update_user, last_update_timestamp, last_update_remark
from cal_rota


    and begin_office_date = #{begin_office_date,jdbcType=DATE}


    and end_office_date = #{end_office_date,jdbcType=DATE}


    and begin_time = #{begin_time,jdbcType=CHAR}


    and end_time = #{end_time,jdbcType=CHAR}


    and comp_id = #{comp_id,jdbcType=INTEGER}


    and stat_id = #{stat_id,jdbcType=INTEGER}


    and area_id = #{area_id,jdbcType=INTEGER}


    and group_id = #{group_id,jdbcType=INTEGER}


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and rota_level = #{rota_level,jdbcType=CHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}
