mapper.delete:

delete from cal_survey_dispatch


    and dispatch_code = #{dispatch_code,jdbcType=NVARCHAR}


    and dispatch_person = #{dispatch_person,jdbcType=NVARCHAR}


    and dispatch_datetime = #{dispatch_datetime,jdbcType=TIMESTAMP}


    and dispatch_to = #{dispatch_to,jdbcType=NVARCHAR}


    and survey_dispatch_desc = #{survey_dispatch_desc,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, dispatch_code, dispatch_person, dispatch_datetime, dispatch_to, survey_dispatch_desc, 
enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark
from cal_survey_dispatch


    and dispatch_code = #{dispatch_code,jdbcType=NVARCHAR}


    and dispatch_person = #{dispatch_person,jdbcType=NVARCHAR}


    and dispatch_datetime = #{dispatch_datetime,jdbcType=TIMESTAMP}


    and dispatch_to = #{dispatch_to,jdbcType=NVARCHAR}


    and survey_dispatch_desc = #{survey_dispatch_desc,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}
