mapper.delete:

delete from item_outbound_order


    and erp_contract_code = #{erp_contract_code,jdbcType=NVARCHAR}


    and order_no = #{order_no,jdbcType=NVARCHAR}


    and outbound_order = #{outbound_order,jdbcType=NVARCHAR}


    and request_person = #{request_person,jdbcType=NVARCHAR}


    and request_date = #{request_date,jdbcType=DATE}


    and outbound_status = #{outbound_status,jdbcType=CHAR}


    and item_code = #{item_code,jdbcType=NVARCHAR}


    and total_num = #{total_num,jdbcType=DECIMAL}


    and available_num = #{available_num,jdbcType=DECIMAL}


    and to_submit_num = #{to_submit_num,jdbcType=DECIMAL}


    and standby_num = #{standby_num,jdbcType=DECIMAL}


    and to_audit_num = #{to_audit_num,jdbcType=DECIMAL}


    and audit_num = #{audit_num,jdbcType=DECIMAL}


