mapper.select:

select id, erp_contract_code, order_no, outbound_order, request_person, request_date, 
outbound_status, item_code, total_num, available_num, to_submit_num, standby_num, 
to_audit_num, audit_num, asset_num, outbound_datetime, pick_person, data_source, 
siebel_import_status, outbound_type, qrcode_no, enable_flag, create_user, create_timestamp, 
last_update_user, last_update_timestamp, last_update_remark, sync_datetime, source_id
from item_outbound_order


    and erp_contract_code = #{erp_contract_code,jdbcType=NVARCHAR}


