mapper.delete:

delete from mt_maintenance_factor


    and factor_code = #{factor_code,jdbcType=NVARCHAR}


    and factor_column = #{factor_column,jdbcType=NVARCHAR}


    and factor_value_type = #{factor_value_type,jdbcType=CHAR}


    and factor_evaluation = #{factor_evaluation,jdbcType=NVARCHAR}


    and factor_evaluation_desc = #{factor_evaluation_desc,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, factor_code, factor_column, factor_value_type, factor_evaluation, factor_evaluation_desc, 
enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark
from mt_maintenance_factor


    and factor_code = #{factor_code,jdbcType=NVARCHAR}


    and factor_column = #{factor_column,jdbcType=NVARCHAR}


    and factor_value_type = #{factor_value_type,jdbcType=CHAR}


    and factor_evaluation = #{factor_evaluation,jdbcType=NVARCHAR}


    and factor_evaluation_desc = #{factor_evaluation_desc,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}
