mapper.delete:

delete from mt_template


    and template_no = #{template_no,jdbcType=NVARCHAR}


    and template_name = #{template_name,jdbcType=NVARCHAR}


    and template_level = #{template_level,jdbcType=CHAR}


    and template_status = #{template_status,jdbcType=CHAR}


    and template_work_cycle = #{template_work_cycle,jdbcType=DECIMAL}


    and effect_date = #{effect_date,jdbcType=DATE}


    and expire_date = #{expire_date,jdbcType=DATE}


    and emp_level_1 = #{emp_level_1,jdbcType=CHAR}


    and emp_level_2 = #{emp_level_2,jdbcType=CHAR}


    and emp_level_3 = #{emp_level_3,jdbcType=CHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, template_no, template_name, template_level, template_status, template_work_cycle, 
effect_date, expire_date, emp_level_1, emp_level_2, emp_level_3, enable_flag, create_user, 
create_timestamp, last_update_user, last_update_timestamp, last_update_remark
from mt_template


    and template_no = #{template_no,jdbcType=NVARCHAR}


    and template_name = #{template_name,jdbcType=NVARCHAR}


    and template_level = #{template_level,jdbcType=CHAR}


    and template_status = #{template_status,jdbcType=CHAR}


    and template_work_cycle = #{template_work_cycle,jdbcType=DECIMAL}


    and effect_date = #{effect_date,jdbcType=DATE}


    and expire_date = #{expire_date,jdbcType=DATE}


    and emp_level_1 = #{emp_level_1,jdbcType=CHAR}


    and emp_level_2 = #{emp_level_2,jdbcType=CHAR}


    and emp_level_3 = #{emp_level_3,jdbcType=CHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}
