mapper.delete:

delete from pos_emp_position


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and longitude = #{longitude,jdbcType=NVARCHAR}


    and latitude = #{latitude,jdbcType=NVARCHAR}


    and order_address = #{order_address,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and position_type = #{position_type,jdbcType=CHAR}


    and resource_order = #{resource_order,jdbcType=NVARCHAR}


    and position_flag = #{position_flag,jdbcType=CHAR}


    and imsi = #{imsi,jdbcType=NVARCHAR}

mapper.select:

select id, emp_code, longitude, latitude, order_address, enable_flag, create_user, 
create_timestamp, last_update_user, last_update_timestamp, last_update_remark, position_type, 
resource_order, position_flag, imsi
from pos_emp_position


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and longitude = #{longitude,jdbcType=NVARCHAR}


    and latitude = #{latitude,jdbcType=NVARCHAR}


    and order_address = #{order_address,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and position_type = #{position_type,jdbcType=CHAR}


    and resource_order = #{resource_order,jdbcType=NVARCHAR}


    and position_flag = #{position_flag,jdbcType=CHAR}


    and imsi = #{imsi,jdbcType=NVARCHAR}
