mapper.delete:

delete from sys_parameter


    and param_name = #{param_name,jdbcType=VARCHAR}


    and param_key = #{param_key,jdbcType=VARCHAR}


    and param_value = #{param_value,jdbcType=VARCHAR}


    and param_desc = #{param_desc,jdbcType=VARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=VARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=DATE}


    and last_update_user = #{last_update_user,jdbcType=VARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=DATE}


    and last_update_remark = #{last_update_remark,jdbcType=VARCHAR}

mapper.select:

select id, param_name, param_key, param_value, param_desc, enable_flag, create_user, 
create_timestamp, last_update_user, last_update_timestamp, last_update_remark
from sys_parameter


    and param_name = #{param_name,jdbcType=VARCHAR}


    and param_key = #{param_key,jdbcType=VARCHAR}


    and param_value = #{param_value,jdbcType=VARCHAR}


    and param_desc = #{param_desc,jdbcType=VARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=VARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=DATE}


    and last_update_user = #{last_update_user,jdbcType=VARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=DATE}


    and last_update_remark = #{last_update_remark,jdbcType=VARCHAR}
