mapper.delete:

delete from base_dictionary


    and dict_type = #{dict_type,jdbcType=NVARCHAR}


    and dict_type_name = #{dict_type_name,jdbcType=NVARCHAR}


    and dict_name = #{dict_name,jdbcType=NVARCHAR}


    and dict_code = #{dict_code,jdbcType=NVARCHAR}


    and dict_sort = #{dict_sort,jdbcType=INTEGER}


    and p_dict_type = #{p_dict_type,jdbcType=NVARCHAR}


    and p_dict_code = #{p_dict_code,jdbcType=NVARCHAR}


    and dict_additional_value = #{dict_additional_value,jdbcType=NVARCHAR}


    and dict_remark = #{dict_remark,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, dict_type, dict_type_name, dict_name, dict_code, dict_sort, p_dict_type, 
p_dict_code, dict_additional_value, dict_remark, enable_flag, create_user, create_timestamp, 
last_update_user, last_update_timestamp, last_update_remark
from base_dictionary


    and dict_type = #{dict_type,jdbcType=NVARCHAR}


    and dict_type_name = #{dict_type_name,jdbcType=NVARCHAR}


    and dict_name = #{dict_name,jdbcType=NVARCHAR}


    and dict_code = #{dict_code,jdbcType=NVARCHAR}


    and dict_sort = #{dict_sort,jdbcType=INTEGER}


    and p_dict_type = #{p_dict_type,jdbcType=NVARCHAR}


    and p_dict_code = #{p_dict_code,jdbcType=NVARCHAR}


    and dict_additional_value = #{dict_additional_value,jdbcType=NVARCHAR}


    and dict_remark = #{dict_remark,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}
