mapper.delete:

delete from base_emp_certificate


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and certificate_name = #{certificate_name,jdbcType=NVARCHAR}


    and certificate_level = #{certificate_level,jdbcType=NVARCHAR}


    and certificate_unit = #{certificate_unit,jdbcType=NVARCHAR}


    and effect_date = #{effect_date,jdbcType=DATE}


    and certificate_date = #{certificate_date,jdbcType=DATE}


    and certificate_code = #{certificate_code,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}

mapper.select:

select id, emp_code, certificate_name, certificate_level, certificate_unit, effect_date, 
certificate_date, certificate_code, enable_flag, create_user, create_timestamp, last_update_user, 
last_update_timestamp, last_update_remark, sync_datetime, source_id
from base_emp_certificate


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and certificate_name = #{certificate_name,jdbcType=NVARCHAR}


    and certificate_level = #{certificate_level,jdbcType=NVARCHAR}


    and certificate_unit = #{certificate_unit,jdbcType=NVARCHAR}


    and effect_date = #{effect_date,jdbcType=DATE}


    and certificate_date = #{certificate_date,jdbcType=DATE}


    and certificate_code = #{certificate_code,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}
