mapper.delete:

delete from cal_complaint_trace


    and complaint_code = #{complaint_code,jdbcType=NVARCHAR}


    and trace_num = #{trace_num,jdbcType=NVARCHAR}


    and deal_person = #{deal_person,jdbcType=NVARCHAR}


    and authorization_person = #{authorization_person,jdbcType=NVARCHAR}


    and trace_link = #{trace_link,jdbcType=NVARCHAR}


    and start_datetime = #{start_datetime,jdbcType=NVARCHAR}


    and finish_datetime = #{finish_datetime,jdbcType=NVARCHAR}


    and cost_time = #{cost_time,jdbcType=NVARCHAR}


    and trace_desc = #{trace_desc,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}

mapper.select:

select id, complaint_code, trace_num, deal_person, authorization_person, trace_link, 
start_datetime, finish_datetime, cost_time, trace_desc, sync_datetime
from cal_complaint_trace


    and complaint_code = #{complaint_code,jdbcType=NVARCHAR}


    and trace_num = #{trace_num,jdbcType=NVARCHAR}


    and deal_person = #{deal_person,jdbcType=NVARCHAR}


    and authorization_person = #{authorization_person,jdbcType=NVARCHAR}


    and trace_link = #{trace_link,jdbcType=NVARCHAR}


    and start_datetime = #{start_datetime,jdbcType=NVARCHAR}


    and finish_datetime = #{finish_datetime,jdbcType=NVARCHAR}


    and cost_time = #{cost_time,jdbcType=NVARCHAR}


    and trace_desc = #{trace_desc,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}
