mapper.delete:

delete from cal_fault_duty_param


    and fault_def = #{fault_def,jdbcType=NVARCHAR}


    and bad_code = #{bad_code,jdbcType=NVARCHAR}


    and bad_category = #{bad_category,jdbcType=NVARCHAR}


    and bad_class = #{bad_class,jdbcType=NVARCHAR}


    and is_fault = #{is_fault,jdbcType=NVARCHAR}


    and responsibility_attribution = #{responsibility_attribution,jdbcType=NVARCHAR}


    and responsibility_type = #{responsibility_type,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}

mapper.select:

select id, fault_def, bad_code, bad_category, bad_class, is_fault, responsibility_attribution, 
responsibility_type, enable_flag
from cal_fault_duty_param


    and fault_def = #{fault_def,jdbcType=NVARCHAR}


    and bad_code = #{bad_code,jdbcType=NVARCHAR}


    and bad_category = #{bad_category,jdbcType=NVARCHAR}


    and bad_class = #{bad_class,jdbcType=NVARCHAR}


    and is_fault = #{is_fault,jdbcType=NVARCHAR}


    and responsibility_attribution = #{responsibility_attribution,jdbcType=NVARCHAR}


    and responsibility_type = #{responsibility_type,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}
