mapper.delete:

delete from ch_regular_check_emp


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and emp_education = #{emp_education,jdbcType=NVARCHAR}


    and current_position = #{current_position,jdbcType=NVARCHAR}


    and current_position_datetime = #{current_position_datetime,jdbcType=DATE}


    and elevator_model = #{elevator_model,jdbcType=NVARCHAR}


    and escalator_models = #{escalator_models,jdbcType=NVARCHAR}


    and apply_for_elevator = #{apply_for_elevator,jdbcType=CHAR}


    and apply_for_escalator = #{apply_for_escalator,jdbcType=CHAR}


    and is_use_pda = #{is_use_pda,jdbcType=CHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}

mapper.select:

select id, emp_code, emp_education, current_position, current_position_datetime, 
elevator_model, escalator_models, apply_for_elevator, apply_for_escalator, is_use_pda, 
enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark
from ch_regular_check_emp


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and emp_education = #{emp_education,jdbcType=NVARCHAR}


    and current_position = #{current_position,jdbcType=NVARCHAR}


    and current_position_datetime = #{current_position_datetime,jdbcType=DATE}


    and elevator_model = #{elevator_model,jdbcType=NVARCHAR}


    and escalator_models = #{escalator_models,jdbcType=NVARCHAR}


    and apply_for_elevator = #{apply_for_elevator,jdbcType=CHAR}


    and apply_for_escalator = #{apply_for_escalator,jdbcType=CHAR}


    and is_use_pda = #{is_use_pda,jdbcType=CHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


