mapper.delete:

delete from item_info


    and item_code = #{item_code,jdbcType=NVARCHAR}


    and item_name = #{item_name,jdbcType=NVARCHAR}


    and item_draw_no = #{item_draw_no,jdbcType=NVARCHAR}


    and item_spec = #{item_spec,jdbcType=NVARCHAR}


    and item_unit = #{item_unit,jdbcType=NVARCHAR}


    and item_features = #{item_features,jdbcType=NVARCHAR}


    and item_desc = #{item_desc,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}

mapper.select:

select id, item_code, item_name, item_draw_no, item_spec, item_unit, item_features, 
item_desc, enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark, sync_datetime, source_id
from item_info


    and item_code = #{item_code,jdbcType=NVARCHAR}


    and item_name = #{item_name,jdbcType=NVARCHAR}


    and item_draw_no = #{item_draw_no,jdbcType=NVARCHAR}


    and item_spec = #{item_spec,jdbcType=NVARCHAR}


    and item_unit = #{item_unit,jdbcType=NVARCHAR}


    and item_features = #{item_features,jdbcType=NVARCHAR}


    and item_desc = #{item_desc,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}
