mapper.delete:

delete from item_parameter_attach


    and item_param_id = #{item_param_id,jdbcType=INTEGER}


    and attach_name = #{attach_name,jdbcType=NVARCHAR}


    and attach_url = #{attach_url,jdbcType=NVARCHAR}


    and attach_remark = #{attach_remark,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}

mapper.select:

select id, item_param_id, attach_name, attach_url, attach_remark, enable_flag, create_user, 
create_timestamp, last_update_user, last_update_timestamp, last_update_remark, sync_datetime, 
source_id
from item_parameter_attach


    and item_param_id = #{item_param_id,jdbcType=INTEGER}


    and attach_name = #{attach_name,jdbcType=NVARCHAR}


    and attach_url = #{attach_url,jdbcType=NVARCHAR}


    and attach_remark = #{attach_remark,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and sync_datetime = #{sync_datetime,jdbcType=TIMESTAMP}


    and source_id = #{source_id,jdbcType=NVARCHAR}
