mapper.delete:

delete from sys_menu


    and menu_name = #{menu_name,jdbcType=VARCHAR}


    and menu_url = #{menu_url,jdbcType=VARCHAR}


    and menu_sort = #{menu_sort,jdbcType=INTEGER}


    and menu_level = #{menu_level,jdbcType=INTEGER}


    and p_menu_id = #{p_menu_id,jdbcType=INTEGER}


    and c_menu_flag = #{c_menu_flag,jdbcType=CHAR}


    and menu_arrange = #{menu_arrange,jdbcType=CHAR}


    and menu_sa = #{menu_sa,jdbcType=CHAR}


    and menu_icon = #{menu_icon,jdbcType=VARCHAR}


    and menu_remark = #{menu_remark,jdbcType=VARCHAR}

mapper.select:

select id, menu_name, menu_url, menu_sort, menu_level, p_menu_id, c_menu_flag, menu_arrange, 
menu_sa, menu_icon, menu_remark
from sys_menu


    and menu_name = #{menu_name,jdbcType=VARCHAR}


    and menu_url = #{menu_url,jdbcType=VARCHAR}


    and menu_sort = #{menu_sort,jdbcType=INTEGER}


    and menu_level = #{menu_level,jdbcType=INTEGER}


    and p_menu_id = #{p_menu_id,jdbcType=INTEGER}


    and c_menu_flag = #{c_menu_flag,jdbcType=CHAR}


    and menu_arrange = #{menu_arrange,jdbcType=CHAR}


    and menu_sa = #{menu_sa,jdbcType=CHAR}


    and menu_icon = #{menu_icon,jdbcType=VARCHAR}


    and menu_remark = #{menu_remark,jdbcType=VARCHAR}
