mapper.insert:

insert into sys_user (sso_code, user_passwd, emp_code, 
  enable_flag, create_user, create_timestamp, 
  last_update_user, last_update_timestamp, last_update_remark
  )
values (#{sso_code,jdbcType=VARCHAR}, #{user_passwd,jdbcType=VARCHAR}, #{emp_code,jdbcType=VARCHAR}, 
  #{enable_flag,jdbcType=CHAR}, #{create_user,jdbcType=VARCHAR}, #{create_timestamp,jdbcType=DATE}, 
  #{last_update_user,jdbcType=VARCHAR}, #{last_update_timestamp,jdbcType=DATE}, #{last_update_remark,jdbcType=VARCHAR}
  )

mapper.delete:

delete from sys_user


    and sso_code = #{sso_code,jdbcType=VARCHAR}


    and user_passwd = #{user_passwd,jdbcType=VARCHAR}


    and emp_code = #{emp_code,jdbcType=VARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=VARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=DATE}


    and last_update_user = #{last_update_user,jdbcType=VARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=DATE}


    and last_update_remark = #{last_update_remark,jdbcType=VARCHAR}

mapper.select:

select id, sso_code, user_passwd, emp_code, enable_flag, create_user, create_timestamp, 
last_update_user, last_update_timestamp, last_update_remark
from sys_user


    and sso_code = #{sso_code,jdbcType=VARCHAR}


    and user_passwd = #{user_passwd,jdbcType=VARCHAR}


    and emp_code = #{emp_code,jdbcType=VARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=VARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=DATE}


    and last_update_user = #{last_update_user,jdbcType=VARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=DATE}


    and last_update_remark = #{last_update_remark,jdbcType=VARCHAR}
