mapper.delete:

delete from sys_user_prompt


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and comp_id = #{comp_id,jdbcType=INTEGER}


    and stat_id = #{stat_id,jdbcType=INTEGER}


    and area_id = #{area_id,jdbcType=INTEGER}


    and prompt_title = #{prompt_title,jdbcType=NVARCHAR}


    and prompt_type = #{prompt_type,jdbcType=CHAR}


    and prompt_key = #{prompt_key,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and prompt_url = #{prompt_url,jdbcType=NVARCHAR}

mapper.select:

select id, emp_code, comp_id, stat_id, area_id, prompt_title, prompt_type, prompt_key, 
enable_flag, create_user, create_timestamp, last_update_user, last_update_timestamp, 
last_update_remark, prompt_url
from sys_user_prompt


    and emp_code = #{emp_code,jdbcType=NVARCHAR}


    and comp_id = #{comp_id,jdbcType=INTEGER}


    and stat_id = #{stat_id,jdbcType=INTEGER}


    and area_id = #{area_id,jdbcType=INTEGER}


    and prompt_title = #{prompt_title,jdbcType=NVARCHAR}


    and prompt_type = #{prompt_type,jdbcType=CHAR}


    and prompt_key = #{prompt_key,jdbcType=NVARCHAR}


    and enable_flag = #{enable_flag,jdbcType=CHAR}


    and create_user = #{create_user,jdbcType=NVARCHAR}


    and create_timestamp = #{create_timestamp,jdbcType=TIMESTAMP}


    and last_update_user = #{last_update_user,jdbcType=NVARCHAR}


    and last_update_timestamp = #{last_update_timestamp,jdbcType=TIMESTAMP}


    and last_update_remark = #{last_update_remark,jdbcType=NVARCHAR}


    and prompt_url = #{prompt_url,jdbcType=NVARCHAR}
